import React from 'react';
import image from './bgi.jpg';
import ReactDOM from 'react-dom';
import { Link, Url } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';
// import { useRouteLoaderData } from 'react-router-dom';

export const Login = () => {
  return (
      <div className="main">
        <img 
          src="https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350"
          alt="new"
          >

    
        <div style={styles.container}> 
            <form className='box2' style={styles.box}>
                <div className="text" style={{color:"grey"}}>Login</div>
                {/* <label htmlFor="email" className="email" style={styles.label}>Email</label> */}
                <input type="email" className="emailip" style={styles.input} placeholder='Email' />
                {/* <label htmlFor="password" className="password" style={styles.label}>Password</label> */}
                <input type="password" className="password" style={styles.input} placeholder='Password'/>
                <button className="login" style={styles.submit}>Log In</button>
                <p>Don't have an account?<Link to='/Signup'>Sign Up</Link></p>
            </form>
        </div>
        
    </img>
    </div>
  )
}

const styles = {
    label:{
        minHeight:"20px"

    },
    box:{
        width:'400px',
        height:'25vh',
        backgroundsize:'cover',
        backgroundColor:"lightgreen",
        boxsizing: "border-box",
        justifyContent:"center",
        display:"inline-grid",
        padding:"50px"
        
    },
    container:{
        // background: BrowserRouter('https://images.pexels.com/photos/1679551/pexels-photo-1679551.jpeg?cs=srgb&dl=pexels-mirrographer-1679551.jpg&fm=jpg'),
        minHeight: "98vh",
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        backgroundsize:"cover",
        color: "white",
        
        
    },
    submit:{
        backgroundColor:"skyblue",
        color:"white",
        border:"none",
        width:"10vh",
        margin:"auto",
        padding:"5px"
        


    },
    input:{
        height:"20px",
        border:"none",
    }

}

export default Login
